import React, { Component } from "react";
import { Route, Router, Link } from "react-router-dom";
// import Proof from "./Subcomponents/Proof";
// import Anagram from "./Subcomponents/Anagram";
import "./Projects.css";
class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = { anagram: false, proof: false, home: true };
  }
  onClick = (e, prevState) => {
    if ([e.target.name] === "anagram" && prevState.anagram !== false) {
      this.setState({ anagram: true });
    }
    // else {
    //   this.setState({
    //     anagram: true
    //   });
    //   console.log("workedAna");
    // }
    if ([e.target.name] === "proof" && prevState.proof !== false) {
      this.setState({ proof: true });
    }
    // else {
    //   this.setState({
    //     proof: true
    //   });
    // }
    if ([e.target.name] === "home") {
      this.setState({ home: false });
    } else {
      this.setState({
        home: true
      });
    }
  };

  render() {
    let itemStyle;
    let itemHeader;
    let itemDescription;
    let anagramPic = "../Images/tree.jpeg ";
    let homePic = "../Images/cabin.jpg";
    let proofPic = "../Images/5022.png";

    if (this.state.home === true) {
      itemStyle = {
        backgroundImage: "url(" + homePic + ")"
      };
      console.log("working");
    }
    if (this.state.anagram === true) {
      itemStyle = {
        backgroundImage: "url(" + anagramPic + ")"
      };
      console.log(anagramPic);
      console.log("working2");
      itemHeader = "Try Anagram Generator";

      itemDescription =
        "This app takes text input, sends a fetch request to an API which returns it's closest match to an anagram.";
    }

    return (
      <div className="projectMain">
        <div className="sidebar">
          <button className="projectButton" name="home" onClick={this.onClick}>
            My Projects
          </button>
          <ul>
            <li>
              <button
                className="sideName"
                name="anagram"
                id="anagramButton"
                onClick={this.onClick}
              >
                Anagram Generator
              </button>
            </li>
            <li>
              <button
                className="sideName"
                name="proof"
                id="proofButton"
                onClick={this.onClick}
              >
                Proof Technology Webpage
              </button>
            </li>
          </ul>
        </div>
        <div className="mainItem">
          <div className="itemImage" style={itemStyle}>
            {""}
          </div>
          <h3>{itemHeader}</h3>
          <p>{itemDescription}</p>
        </div>
      </div>
    );
  }
}
export default Projects;
